    ("CA", "http://www.servicenl.gov.nl.ca/registries/companies.html", "CA_NL-NFL_TNL", "Registry of Companies, Department of Government Services", "This entry was imported from the Open Corporates Jurisdiction List."),
    ("CA", "http://novascotia.ca/sns/access/business/registry-joint-stock-companies.asp", "CA_NS-NVS_NVE", "Nova Scotia Registry of Joint Stock Companies ", "This entry was imported from the Open Corporates Jurisdiction List."),
    ("CA", "https://www.justice.gov.nt.ca/en/divisions/legal-registries-division/corporate-registries/", "CA_NT-NWT_TNO", "Canadian Provincial Corporate Registration - Northwest Territories ", "This entry was imported from the Open Corporates Jurisdiction List."),
    ("CA", "http://nunavutlegalregistries.ca/cr_index_en.shtml", "CA_NU-NNV", "Nunavut Department of Justice - Corporate Registries", "This entry was imported from the Open Corporates Jurisdiction List."),
    ("CA", "https://www.ontario.ca/page/business-and-economy", "CA_ON-ONT", "ServiceOntario, Ministry of Government Services ", "This entry was imported from the Open Corporates Jurisdiction List."),
    ("CA", "http://www.gov.pe.ca/corporations/index.php", "CA_PE-PEI_IPE", "Prince Edward Island Corporate", "This entry was imported from the Open Corporates Jurisdiction List."),
    ("CA", "http://www.registreentreprises.gouv.qc.ca/", "CA_QC-QBC", "Quebec Business Registrar ", "This entry was imported from the Open Corporates Jurisdiction List."),
